import Navbar from "./components/Navbar"
import "./Blog.css"
import ReactDOM, { render } from "react-dom"
import BlogEntries from "./components/BlogEntries"
import { useState, useEffect, Component } from "react";
import axios from 'axios';


export default class Blog extends Component{
    // let [newTextArrayEntry, setNewTextArrayEntry] = useState([])
    // let [blogPost, setBlogPost] = useState([])
    // let currentEntry

    constructor(props){
      super(props);
      this.onChangeBlogPost = this.onChangeBlogPost.bind(this)

      this.state = {
        blogpost: '',
      }
    }

    componentDidMount(){
      this.setState({
        blogpost: 'Help me please'
      })
    }

    onChangeBlogPost(e){
      e.preventDefault()
      this.setState({
        blogpost: e.target.value
      })
    }

    onFormSubmit(e){
      e.preventDefault()

      const blogPost = {
        blogpost: this.state.blogpost
      }

      console.log(blogPost)
    }

    // useEffect(()=>{
    //   axios.get("http://localhost:5000/blogposts")
    //   .then((res) =>{
    //     setBlogPost(res.data[0].postContent)
    //   })
    // },[])

    render(){
      return(
          <div>
              <Navbar />
              <form onSubmit={this.onFormSubmit} id="blog-form" className="all-blog-data">
                  <input onChange={this.onChangeBlogPost} id="blog-entry" type="text" className="blog-entry" />
                  <button type="submit" id="blog-entry-submit-button">Submit</button>
              </form>

              <p className="blog-entries"></p>

              {
                this.state.blogPost.map((user) =>{
                  return <p className="blog-entries">{user}</p>
                })
              }

              {/* <p className="blog-entries">{newTextArrayEntry}</p> */}
              {/* <BlogEntries newTextArrayEntry={newTextArrayEntry} /> */}
          </div>
      )
    }

}
