import Navbar from "./components/Navbar"
import "./Blog.css"
import ReactDOM from "react-dom"
import BlogEntries from "./components/BlogEntries"
import { useState, useEffect } from "react";
import axios from 'axios';

export default function Blog(){
    let [newTextArrayEntry, setNewTextArrayEntry] = useState([])
    let [test1, setTest1] = useState("")
    let currentEntry

    function onBlogSubmit(e){
        currentEntry = document.getElementById('blog-entry').value
        e.preventDefault()
        setNewTextArrayEntry(prevState => [...prevState, currentEntry])
        document.getElementById('blog-entry').value = ""

    }

    useEffect(() =>{
        axios.get("http://localhost:5000")
        .then((response) =>{
          setTest1(response.data.title)

        })
      }, [])

    async function postTest1(e){
        e.preventDefault()

        try {
          await axios.post("mongodb+srv://Vastagon:Vastagon1@taekwondobulletincluste.7nvk4.mongodb.net/TaekwondoBulletinCluster?retryWrites=true&w=majority", {
            test1
          })
        } catch (error) {
          console.log(error)
        }
      }

    return(
        <div>
            <Navbar />
            <form onSubmit={postTest1} id="blog-form" className="all-blog-data">
                <input id="blog-entry" type="text" className="blog-entry" />
                <button type="submit" id="blog-entry-submit-button">Submit</button>
            </form>

            <p>{test1}</p>



            {/* <p className="blog-entries">{newTextArrayEntry}</p> */}
            {/* <BlogEntries newTextArrayEntry={newTextArrayEntry} /> */}
        </div>
    )

}
