import Navbar from "./components/Navbar"
import "./Blog.css"
import ReactDOM from "react-dom"
import BlogEntries from "./components/BlogEntries"
import { useState, useEffect } from "react";
import axios from 'axios';

const router = require('express').Router()

export default function Blog(){
    let [newTextArrayEntry, setNewTextArrayEntry] = useState([])
    let [blogPost, setBlogPost] = useState([])
    let currentEntry

    function onBlogSubmit(e){
      e.preventDefault()
      currentEntry = document.getElementById('blog-entry').value

      useEffect(() =>{
        router.route('/add').post((req,res) =>{
          const postContent = req.body.postContent

          const newBlogpost = new BlogPost({
              postContent,
          })

          newBlogpost.save()///Mongoose again
          .then(() => res.json('Post Added'))
          .catch(err => res.status(400).json(`Error: ${err}`))
      })

      })




    }

    useEffect(()=>{
      axios.get("http://localhost:5000/blogposts")
      .then((res) =>{
        setBlogPost(res.data[0].postContent)
      })
    },[])


    return(
        <div>
            <Navbar />
            <form id="blog-form" className="all-blog-data">
                <input id="blog-entry" type="text" className="blog-entry" />
                <button type="submit" id="blog-entry-submit-button">Submit</button>
            </form>

            <p className="blog-entries">{blogPost}</p>



            {/* <p className="blog-entries">{newTextArrayEntry}</p> */}
            {/* <BlogEntries newTextArrayEntry={newTextArrayEntry} /> */}
        </div>
    )

}
